import { ProjectCard3D } from "@/components/ProjectCard3D";
import { useIntersectionObserver } from "@/hooks/useIntersectionObserver";
import fifthVideo from "@assets/5th_1764001382378.mp4";

export function ProjectsSection() {
  const { ref, isVisible } = useIntersectionObserver({ threshold: 0.1 });

  const projects = [
    {
      title: "Task Management Pro",
      description: "A comprehensive Android task management application with cloud sync, notifications, and collaborative features.",
      tags: ["Kotlin", "Jetpack Compose", "Room", "Firebase"],
      image: "",
    },
    {
      title: "Weather Insights",
      description: "Beautiful weather app with detailed forecasts, interactive maps, and personalized weather alerts.",
      tags: ["Android", "Retrofit", "Material Design", "APIs"],
      image: "",
    },
    {
      title: "Portfolio Generator",
      description: "Full-stack web application for creating stunning portfolios with customizable templates and live preview.",
      tags: ["React", "TypeScript", "Node.js", "PostgreSQL"],
      image: "",
    },
    {
      title: "Fitness Tracker",
      description: "Track workouts, set goals, and monitor progress with detailed analytics and achievement system.",
      tags: ["Kotlin", "MVVM", "Room", "Charts"],
      image: "",
    },
  ];

  return (
    <section
      ref={ref}
      id="projects"
      className="relative py-32 px-6 overflow-hidden"
      data-testid="section-projects"
    >
      {/* Full-width video background - Always loaded */}
      <video
        autoPlay
        loop
        muted
        playsInline
        className="absolute inset-0 w-full h-full object-cover pixel-video"
        style={{ pointerEvents: "none" }}
      >
        <source src={fifthVideo} type="video/mp4" />
      </video>

      {/* Integrated overlay for text readability */}
      <div className="absolute inset-0 bg-gradient-to-b from-black/45 via-black/35 to-black/45" />

      {/* Content - Animated on scroll */}
      {isVisible && (
        <div className="relative z-10 max-w-7xl mx-auto animate-section-load">
          <div className="text-center mb-20 animate-fade-in-up animate-section-load">
            <h2 className="font-pixel text-3xl md:text-4xl font-bold text-white/95 mb-4 drop-shadow-lg pixel-shadow">
              FEATURED PROJECTS
            </h2>
            <p className="text-white/80 max-w-2xl mx-auto font-pixel text-xs drop-shadow-md">
              A SHOWCASE OF INNOVATIVE SOLUTIONS AND CREATIVE IMPLEMENTATIONS
            </p>
            <div className="h-1 w-24 mx-auto mt-6 bg-gradient-to-r from-white/60 to-white/30 rounded-full" />
          </div>

          <div className="grid md:grid-cols-2 gap-8">
            {projects.map((project, index) => (
              <div key={index} className="animate-section-load">
                <ProjectCard3D
                  title={project.title}
                  description={project.description}
                  tags={project.tags}
                  image={project.image}
                  index={index}
                  data-testid={`card-project-${index}`}
                />
              </div>
            ))}
          </div>
        </div>
      )}
    </section>
  );
}

