  --sidebar-foreground: 0 0% 88%;
  --sidebar-border: 0 0% 18%;
  --sidebar-primary: 0 0% 60%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 0 0% 18%;
  --sidebar-accent-foreground: 0 0% 85%;
  --sidebar-ring: 0 0% 60%;
  --popover: 0 0% 14%;
  --popover-foreground: 0 0% 90%;
  --popover-border: 0 0% 20%;
  --primary: 0 0% 60%;
  --primary-foreground: 0 0% 100%;
  --secondary: 0 0% 20%;
  --secondary-foreground: 0 0% 85%;
  --muted: 0 0% 22%;
  --muted-foreground: 0 0% 70%;
  --accent: 0 0% 25%;
  --accent-foreground: 0 0% 85%;
  --destructive: 0 58% 42%;
  --destructive-foreground: 0 0% 100%;
  --input: 0 0% 30%;
  --ring: 0 0% 60%;
  --chart-1: 0 0% 60%;
  --chart-2: 0 0% 62%;
  --chart-3: 0 0% 64%;
  --chart-4: 0 0% 66%;
  --chart-5: 0 0% 68%;
  --shadow-2xs: 0px 2px 0px 0px hsl(240 5% 2% / 0.08);
  --shadow-xs: 0px 2px 0px 0px hsl(240 5% 2% / 0.10);
  --shadow-sm: 0px 2px 0px 0px hsl(240 5% 2% / 0.08), 0px 1px 2px -1px hsl(240 5% 2% / 0.12);
  --shadow: 0px 2px 0px 0px hsl(240 5% 2% / 0.10), 0px 1px 2px -1px hsl(240 5% 2% / 0.14);
  --shadow-md: 0px 2px 0px 0px hsl(240 5% 2% / 0.12), 0px 2px 4px -1px hsl(240 5% 2% / 0.16);
  --shadow-lg: 0px 2px 0px 0px hsl(240 5% 2% / 0.14), 0px 4px 6px -1px hsl(240 5% 2% / 0.18);
  --shadow-xl: 0px 2px 0px 0px hsl(240 5% 2% / 0.16), 0px 8px 10px -1px hsl(240 5% 2% / 0.20);
  --shadow-2xl: 0px 2px 0px 0px hsl(240 5% 2% / 0.18);

  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    cursor: none;
  }

  @media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
      animation-duration: 0.01ms !important;
      animation-iteration-count: 1 !important;
      transition-duration: 0.01ms !important;
    }
  }
}

@layer utilities {
  /* Bounce slow animation for landing page */
  @keyframes bounce-slow {
    0%, 100% {
      transform: translateY(0);
    }
    50% {
      transform: translateY(-10px);
    }
  }

  .animate-bounce-slow {
    animation: bounce-slow 3s cubic-bezier(0.68, -0.55, 0.265, 1.55) infinite;
  }

  /* Smooth fade-in and slide-up for section content */
  @keyframes fadeInSlideUp {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-section-load {
    animation: fadeInSlideUp 0.8s ease-out forwards;
  }

  /* Staggered animation for multiple elements */
  .animate-section-load:nth-child(1) {
    animation-delay: 0s;
  }

  .animate-section-load:nth-child(2) {
    animation-delay: 0.1s;
  }

  .animate-section-load:nth-child(3) {
    animation-delay: 0.2s;
  }

  .animate-section-load:nth-child(n+4) {
    animation-delay: 0.3s;
  }

  /* Pixel Typography - Classic Arcade Style */
  .font-pixel {
    font-family: 'Press Start 2P', monospace;
    letter-spacing: 0.05em;
    line-height: 1.4;
  }

  /* Hover elevate */
  .hover-elevate {
    transition: all 0.15s ease-out;
  }

  .hover-elevate:hover {
    transform: translateY(-2px);
  }

  .hover-elevate:active {
    transform: translateY(0);
  }

  /* Active elevate 2 */
  .active-elevate-2:active {
    transform: translateY(1px);
  }

  /* Toggle elevate */
  .toggle-elevate {
    transition: all 0.15s ease-out;
  }

  .toggle-elevate.toggle-elevated {
    transform: translateY(-2px);
  }

  /* Pixel-perfect video rendering */
  .pixel-video {
    image-rendering: pixelated;
    image-rendering: -moz-crisp-edges;
    image-rendering: crisp-edges;
  }

  /* Pixel text shadow for retro effect */
  .pixel-shadow {
    text-shadow: 
      3px 3px 0 rgba(0, 0, 0, 0.3),
      6px 6px 0 rgba(0, 0, 0, 0.15);
  }

  /* Text fade-in animation */
  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  .animate-fade-in {
    animation: fadeIn 0.6s ease-out forwards;
  }

  /* Card slide and fade animation */
  @keyframes cardSlideUp {
    from {
      opacity: 0;
      transform: translateY(20px) scale(0.98);
    }
    to {
      opacity: 1;
      transform: translateY(0) scale(1);
    }
  }

  .animate-card-load {
    animation: cardSlideUp 0.5s cubic-bezier(0.34, 1.56, 0.64, 1) forwards;
  }

  /* Staggered card animation - delay each card */
  .animate-card-load:nth-child(1) {
    animation-delay: 0.1s;
  }

  .animate-card-load:nth-child(2) {
    animation-delay: 0.2s;
  }

  .animate-card-load:nth-child(3) {
    animation-delay: 0.3s;
  }

  .animate-card-load:nth-child(4) {
    animation-delay: 0.4s;
  }

  .animate-card-load:nth-child(5) {
    animation-delay: 0.5s;
  }

  .animate-card-load:nth-child(n+6) {
    animation-delay: 0.6s;
  }

  /* Text slide animation */
  @keyframes textSlideIn {
    from {
      opacity: 0;
      transform: translateX(-10px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  .animate-text-slide {
    animation: textSlideIn 0.5s ease-out forwards;
  }

  /* Line animation - draws line from left to right */
  @keyframes lineGrow {
    from {
      width: 0;
      opacity: 0;
    }
    to {
      width: 100%;
      opacity: 1;
    }
  }

  .animate-line-grow {
    animation: lineGrow 0.7s ease-out forwards;
  }

  /* Pulse animation for interactive elements */
  @keyframes gentlePulse {
    0%, 100% {
      opacity: 1;
    }
    50% {
      opacity: 0.7;
    }
  }

  .animate-pulse-gentle {
    animation: gentlePulse 2s ease-in-out infinite;
  }

  /* Shimmer loading effect */
  @keyframes shimmer {
    0% {
      background-position: -1000px 0;
    }
    100% {
      background-position: 1000px 0;
    }
  }

  .animate-shimmer {
    background: linear-gradient(
      90deg,
      rgba(255, 255, 255, 0) 0%,
      rgba(255, 255, 255, 0.1) 50%,
      rgba(255, 255, 255, 0) 100%
    );
    background-size: 1000px 100%;
    animation: shimmer 2s infinite;
  }

  /* Glowing cursor effect */
  @keyframes cursorGlow {
    0% {
      box-shadow: 0 0 10px rgba(147, 112, 219, 0.6), 0 0 20px rgba(147, 112, 219, 0.3);
    }
    50% {
      box-shadow: 0 0 15px rgba(147, 112, 219, 0.8), 0 0 30px rgba(147, 112, 219, 0.4);
    }
    100% {
      box-shadow: 0 0 10px rgba(147, 112, 219, 0.6), 0 0 20px rgba(147, 112, 219, 0.3);
    }
  }

  @keyframes ringPulse {
    0% {
      transform: scale(1);
      opacity: 0.8;
    }
    100% {
      transform: scale(1.5);
      opacity: 0;
    }
  }

  @keyframes ringPulse2 {
    0% {
      transform: scale(1);
      opacity: 0.6;
    }
    100% {
      transform: scale(2);
      opacity: 0;
    }
  }

  .glowing-cursor {
    position: fixed;
    width: 12px;
    height: 12px;
    pointer-events: none;
    z-index: 9999;
    transform: translate(-50%, -50%);
    opacity: 0;
    transition: opacity 0.15s ease-out;
  }

  .glowing-cursor.visible {
    opacity: 1;
  }

  .cursor-ball {
    position: absolute;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle, rgba(147, 112, 219, 0.9), rgba(147, 112, 219, 0.3));
    border-radius: 50%;
    animation: cursorGlow 2s ease-in-out infinite;
    box-shadow: 0 0 10px rgba(147, 112, 219, 0.6), 0 0 20px rgba(147, 112, 219, 0.3), inset -2px -2px 5px rgba(0, 0, 0, 0.3);
  }

  .cursor-ring {
    position: absolute;
    width: 100%;
    height: 100%;
    border: 2px solid rgba(147, 112, 219, 0.6);
    border-radius: 50%;
    animation: ringPulse 1.5s ease-out infinite;
  }

  .cursor-ring-2 {
    position: absolute;
    width: 100%;
    height: 100%;
    border: 1px solid rgba(147, 112, 219, 0.3);
    border-radius: 50%;
    animation: ringPulse2 2s ease-out infinite;
  }

  /* Dark mode adjustments for cursor */
  .dark .cursor-ball {
    background: radial-gradient(circle, rgba(168, 85, 247, 0.95), rgba(168, 85, 247, 0.3));
    box-shadow: 0 0 15px rgba(168, 85, 247, 0.7), 0 0 25px rgba(168, 85, 247, 0.4), inset -2px -2px 5px rgba(0, 0, 0, 0.5);
  }

  .dark .cursor-ring {
    border-color: rgba(168, 85, 247, 0.7);
  }

  .dark .cursor-ring-2 {
    border-color: rgba(168, 85, 247, 0.4);
  }

  .dark .glowing-cursor {
    animation: cursorGlowDark 2s ease-in-out infinite;
  }

  @keyframes cursorGlowDark {
    0% {
      box-shadow: 0 0 12px rgba(168, 85, 247, 0.7), 0 0 25px rgba(168, 85, 247, 0.35);
    }
    50% {
      box-shadow: 0 0 18px rgba(168, 85, 247, 0.9), 0 0 35px rgba(168, 85, 247, 0.5);
    }
    100% {
      box-shadow: 0 0 12px rgba(168, 85, 247, 0.7), 0 0 25px rgba(168, 85, 247, 0.35);
    }
  }

  /* Landing and Loader animations */
  @keyframes twinkle {
    0%, 100% {
      opacity: 0.3;
    }
    50% {
      opacity: 1;
    }
  }

  .animate-twinkle {
    animation: twinkle 3s ease-in-out infinite;
  }

  @keyframes moveGrid {
    0% {
      transform: translate(0, 0);
    }
    100% {
      transform: translate(50px, 50px);
    }
  }

  @keyframes glow {
    0%, 100% {
      text-shadow: 0 0 10px rgba(34, 211, 238, 0.5), 0 0 20px rgba(34, 211, 238, 0.3);
    }
    50% {
      text-shadow: 0 0 20px rgba(34, 211, 238, 0.8), 0 0 40px rgba(34, 211, 238, 0.6);
    }
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite;
  }

  @keyframes spinReverse {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(-360deg);
    }
  }

  .animate-spin-reverse {
    animation: spinReverse 2s linear infinite;
  }

  @keyframes fadeInOut {
    0% {
      opacity: 0;
    }
    50% {
      opacity: 1;
    }
    100% {
      opacity: 0;
    }
  }

  .animate-fade-in-out {
    animation: fadeInOut 2s ease-in-out infinite;
  }

  /* Smooth card animations for portfolio sections */
  @keyframes slideInFromLeft {
    from {
      opacity: 0;
      transform: translateX(-30px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes slideInFromRight {
    from {
      opacity: 0;
      transform: translateX(30px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes slideInFromTop {
    from {
      opacity: 0;
      transform: translateY(-30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slideInFromBottom {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-slide-in-left {
    animation: slideInFromLeft 0.6s ease-out forwards;
  }

  .animate-slide-in-right {
    animation: slideInFromRight 0.6s ease-out forwards;
  }

  .animate-slide-in-top {
    animation: slideInFromTop 0.6s ease-out forwards;
  }

  .animate-slide-in-bottom {
    animation: slideInFromBottom 0.6s ease-out forwards;
  }

  /* Staggered animations for sections */
  .animate-slide-in-left:nth-child(1) {
    animation-delay: 0s;
  }

  .animate-slide-in-left:nth-child(2) {
    animation-delay: 0.1s;
  }

  .animate-slide-in-left:nth-child(3) {
    animation-delay: 0.2s;
  }

  .animate-slide-in-left:nth-child(n+4) {
    animation-delay: 0.3s;
  }

  /* Scale pop animation */
  @keyframes scalePop {
    0% {
      transform: scale(0.8);
      opacity: 0;
    }
    100% {
      transform: scale(1);
      opacity: 1;
    }
  }

  .animate-scale-pop {
    animation: scalePop 0.4s cubic-bezier(0.34, 1.56, 0.64, 1) forwards;
  }

  /* Gradient shift animation */
  @keyframes gradientShift {
    0%, 100% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
  }

  .animate-gradient-shift {
    animation: gradientShift 3s ease infinite;
  }

  /* Float animation */
  @keyframes floatUp {
    0% {
      transform: translateY(0px);
    }
    50% {
      transform: translateY(-10px);
    }
    100% {
      transform: translateY(0px);
    }
  }

  .animate-float {
    animation: floatUp 3s ease-in-out infinite;
  }

  /* Rotate animation */
  @keyframes rotateRing {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  .animate-rotate-ring {
    animation: rotateRing 4s linear infinite;
  }
}

